mapper.insert:
insert into board
	(
		boardnum
		, board_type
		, title
		, id
		, hits
		, content
	)
values 
	(
		board_seq.nextval	
		, #{board_type}
		, #{title}
		, #{id}
		, #{hits}
		, #{content}
	)

mapper.delete:
delete from
	board
where
	boardnum = #{boardnum}
	and id = #{id}

mapper.update:
update 
	board
set
	title = #{title}
	, content = #{content}
where
	boardnum = #{boardnum}
	and id = #{id}

mapper.insert:
insert into reply (
	replynum
	, content
	, id
	, boardnum
)
values (
	reply_seq.nextval					
	, #{content, jdbcType=VARCHAR}
	, #{id, jdbcType=VARCHAR}
	, #{boardnum}
)

mapper.delete:
delete from
	reply
where
	replynum = #{replynum}
	and id = #{id}

mapper.update:
update
	reply
set
	content = #{content}
where
	replynum = #{replynum}
	and id = #{id}
