mapper.insert:
insert into review (
	reviewnum
	, isbn
	, id
	, content
)
values (
	review_seq.nextval				
	, #{isbn}
	, #{id}
	, #{content}
)

mapper.delete:
delete
from
	review
where
	reviewnum = #{reviewnum}
	and id = #{id}

mapper.update:
update
	review
set
	content = #{content}
where
	reviewnum = #{reviewnum}
	and id = #{id}
