mapper.insert:
insert into community(
	id
	, nickname
	, groupnum
	, group_master
	, groupname
	, groupintroduce
)
values(
	#{id}
	, #{nickname}
	, #{groupnum}
	, 1
	, #{groupname}
	, #{groupintroduce}
)

insert into group_user(
	groupnum
	, groupname
	, groupintroduce
)
values(
	group_user_seq.nextval
	, #{groupname}
	, #{groupintroduce}	
)

insert into community(
	id
	, nickname
	, groupnum
	, groupname
	, groupintroduce
)
values(
	#{id}
	, #{nickname}
	, #{groupnum}
	, #{groupname}
	, #{groupintroduce}
)

mapper.select:
select groupname from group_user
where
groupnum = #{groupnum}

select * from groupreply
where groupnum = #{groupnum}
order by rnum_group desc

mapper.insert:
insert into groupboard(
	groupNum
	, bnum_group
	, title
	, content
	, id
	, nickname
	, groupname
)
values(
	#{groupnum}
	, groupboard_seq.nextval
	, #{title}
	, #{content}
	, #{id}
	, #{nickname}
	, #{groupname}
)

mapper.delete:
delete from groupboard
where
id = #{id}
and
bnum_group = #{bnum_group}

mapper.update:
update groupboard 
set
title = #{title}
, content = #{content}
where
id = #{id}
and bnum_group = #{bnum_group}

mapper.insert:
insert into groupreply(
	groupnum
	, bnum_group
	, rnum_group
	, content
	, id
	, nickname
)values(
	#{groupnum}
	, #{bnum_group}
	, groupreply_seq.nextval
	, #{content}
	, #{id}
	, #{nickname}
)

mapper.select:
select bnum_group from groupreply
where
id = #{id}
and rnum_group = #{rnum_group}

mapper.delete:
delete from groupreply
where
id = #{id}
and
rnum_group = #{rnum_group}
