mapper.insert:
insert into comments (
	commentnum
	, id
	, content
	, booknum
)
values (
	comments_seq.nextval				
	, #{commentnum}
	, #{id}
	, #{content}
	, #{booknum}
)

mapper.delete:
delete
from
	comments
where
	commentnum = #{commentnum}
	and id = #{id}

mapper.update:
update
	comments
set
	content = #{content}
where
	commentnum = #{commentnum}
	and id = #{id}

mapper.insert:
insert
into
    habit
    (
        id
        , time
    )
values
    (
        #{id}
        , #{time}
    )
